import React, { useState, useEffect } from "react";
import axios from "axios";

const storedJwt = localStorage.getItem("token");
const refreshJwt = localStorage.getItem("token-refresh");
const AuthAxios = axios.create({
  headers: {
    Authorization: `Bearer ${storedJwt}`,
  },
});
const AuthRefresh = axios.create({
  headers: {
    Authorization: `Bearer ${refreshJwt}`,
  },
});

export default function ProtectedView() {
  const [data, setData] = useState({});

  async function fecthData(token) {
    const data = await token("/tod/todos/").then(
      (response) => {
        setData(...response.data);
        console.log(response.data);
      },
      (error) => {
        if (error) {
          fecthData(AuthRefresh);
        }
      }
    );
  }

  return (
    <div className="container">
      <div className="columns">
        <div className="column">
          <button
            onClick={() => fecthData(AuthAxios)}
            className="is-button is-warning"
          >
            Obtener data
          </button>
          <h1>{data.title}</h1>
          <h1>{data.description}</h1>
          <h1>
            Â¿La tarea ha sido completada?
            <br />
            {data.completed ? "Si" : "No"}
          </h1>
        </div>
      </div>
    </div>
  );
}
