import React, { useState } from "react";
import axios from "axios";
const axiosInstance = axios.create();
const storedJwt = localStorage.getItem("token");

// (req) => {
//   return req;
// },

export default function ProtectedView() {
  const [data, setData] = useState({});
  const [token, setToken] = useState(storedJwt);

  axiosInstance.interceptors.request.use(async function reFetch() {
    await axios({
      method: "get",
      url: "tod/todos/",
      headers: { Authorization: `Bearer ${storedJwt}` },
    }).then(
      (response) => {
        console.log(response);
      },
      (error) => {
        console.log(error.response.status);
      }
    );
  });
  async function fecthData() {
    await axios({
      method: "get",
      url: "tod/todos/",
      headers: { Authorization: `Bearer ${storedJwt}` },
    }).then(
      (response) => {
        console.log(response);
      },
      (error) => {
        console.log(`Error Code: ${error.response.status}`);
        if (error.response.status === 401) {
          setData([]);
        }
      }
    );
  }

  return (
    <div className="container">
      <div className="columns">
        <div className="column">
          <button onClick={fecthData} className="button is-warning">
            Obtener data
          </button>
          <h1>{data.title}</h1>
          <h1>{data.description}</h1>
          <h1>
            Â¿La tarea ha sido completada?
            <br />
            {data.completed ? "Si" : "No"}
          </h1>
        </div>
      </div>
    </div>
  );
}
